import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { ComposableMap, Geographies, Geography } from 'react-simple-maps';

// United States GeoJSON
const geoUrl = "https://cdn.jsdelivr.net/npm/us-atlas@3/states-10m.json";

/**
 * RegionalData component with React Simple Maps for geography and interactive SVG overlay
 */
const RegionalData = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [hoveredState, setHoveredState] = useState(null);
  const [selectedIndicator, setSelectedIndicator] = useState('MSPUS'); // Default to Median House Price
  const [stateColors, setStateColors] = useState({});
  const [allStatesData, setAllStatesData] = useState({});
  const [indicatorData, setIndicatorData] = useState({});
  const mapRef = useRef(null);

  // Available indicators
  const indicators = [
    { id: 'UNRATE', name: 'Unemployment Rate' },
    { id: 'MSPUS', name: 'Median House Price' },
    { id: 'PCPI', name: 'Per Capita Personal Income' }
  ];

  // Initial data fetch for all indicators
  useEffect(() => {
    const fetchAllIndicators = async () => {
      try {
        setLoading(true);

        // Fetch data for all three indicators in parallel
        const indicatorPromises = indicators.map(indicator => 
          axios.get(`/api/regional/${indicator.id}`)
        );

        const responses = await Promise.all(indicatorPromises);

        // Process data for all indicators
        const allIndicatorData = {};

        responses.forEach((response, index) => {
          const indicator = indicators[index].id;
          allIndicatorData[indicator] = response.data;
        });

        setIndicatorData(allIndicatorData);

        // Update displayed indicator
        updateDisplayedIndicator(selectedIndicator, allIndicatorData);

        setLoading(false);
      } catch (err) {
        console.error('Error fetching regional data:', err);
        setError(`Failed to load regional data: ${err.message}`);
        setLoading(false);
      }
    };

    fetchAllIndicators();
  }, []); // Only run once on component mount

  // Update displayed indicator when selection changes
  useEffect(() => {
    if (Object.keys(indicatorData).length > 0) {
      updateDisplayedIndicator(selectedIndicator, indicatorData);
    }
  }, [selectedIndicator]);

  // Function to update the displayed indicator
  const updateDisplayedIndicator = (indicator, data) => {
    if (!data[indicator] || !data[indicator].states) {
      console.warn(`No data available for indicator: ${indicator}`);
      return;
    }

    const currentData = data[indicator];
    const stateData = {};
    const colorData = {};

    // Get min/max values for normalization
    const values = currentData.states
      .filter(state => state.value !== null && state.value !== undefined)
      .map(state => state.value);

    if (values.length === 0) {
      console.warn("No valid values found in state data");
      return;
    }

    const minValue = Math.min(...values);
    const maxValue = Math.max(...values);
    const valueRange = maxValue - minValue;

    console.log(`${indicator} range:`, minValue, "to", maxValue);

    // Process each state
    currentData.states.forEach(state => {
      // Store the state data
      stateData[state.code] = state;

      // Calculate color
      if (state.value !== null && state.value !== undefined) {
        // Normalize value between 0 and 1
        const normalizedValue = valueRange > 0 
          ? (state.value - minValue) / valueRange 
          : 0.5; // Default to middle value if range is 0

        // Generate color based on indicator
        let color;
        if (indicator === 'UNRATE') {
          // For unemployment, red is high (bad), green is low (good)
          const r = Math.floor(normalizedValue * 255);
          const g = Math.floor((1 - normalizedValue) * 255);
          color = `rgb(${r}, ${g}, 0)`;
        } else {
          // For income/housing prices, green is high (good), red is low (bad)
          const r = Math.floor((1 - normalizedValue) * 255);
          const g = Math.floor(normalizedValue * 255);
          color = `rgb(${r}, ${g}, 0)`;
        }

        colorData[state.code] = { color, value: state.value };
      } else {
        colorData[state.code] = { color: '#D3D3D3', value: null }; // Gray for no data
      }
    });

    setAllStatesData(stateData);
    setStateColors(colorData);
  };

  // Handle indicator change
  const handleIndicatorChange = (e) => {
    setSelectedIndicator(e.target.value);
  };

  // Format value based on indicator type
  const formatValue = (value, indicator) => {
    if (value === null || value === undefined) return 'No data available';

    switch (indicator) {
      case 'UNRATE':
        return `${value.toFixed(1)}%`;
      case 'MSPUS':
        return `$${value.toLocaleString()}.00`;
      case 'PCPI':
        return `$${value.toLocaleString()}.00`;
      default:
        return value.toLocaleString();
    }
  };

  // Get indicator friendly name
  const getIndicatorName = (indicator) => {
    const ind = indicators.find(i => i.id === indicator);
    return ind ? ind.name : indicator;
  };

  // Get all values for a state
  const getAllStateValues = (stateCode) => {
    const result = {};

    indicators.forEach(indicator => {
      if (
        indicatorData[indicator.id] && 
        indicatorData[indicator.id].states
      ) {
        const stateInfo = indicatorData[indicator.id].states.find(
          state => state.code === stateCode
        );

        if (stateInfo) {
          result[indicator.id] = {
            value: stateInfo.value,
            formatted: formatValue(stateInfo.value, indicator.id),
            date: stateInfo.date
          };
        }
      }
    });

    return result;
  };

  // Show state info
  const renderStateInfo = () => {
    if (!hoveredState || !allStatesData[hoveredState]) {
      return (
        <div className="default-info">
          <h3>Hover over a state to view details</h3>
          <p>Currently displaying: {getIndicatorName(selectedIndicator)}</p>
        </div>
      );
    }

    const state = allStatesData[hoveredState];
    const allValues = getAllStateValues(hoveredState);

    return (
      <div className="state-details">
        <h3>{state.name}</h3>

        {/* Primary selected indicator */}
        <div className="state-metric">
          <h4>{getIndicatorName(selectedIndicator)}</h4>
          <div className="metric-value">
            {formatValue(state.value, selectedIndicator)}
          </div>
          <div className="metric-date">
            As of {state.date || 'N/A'}
          </div>
        </div>

        {/* Show other indicators */}
        <div className="additional-metrics">
          <h4>Additional Indicators</h4>
          <div className="metrics-grid">
            {indicators
              .filter(ind => ind.id !== selectedIndicator)
              .map(indicator => (
                allValues[indicator.id] && (
                  <div key={indicator.id} className="metric-item">
                    <div className="metric-name">{indicator.name}:</div>
                    <div className="metric-value">{allValues[indicator.id].formatted}</div>
                  </div>
                )
              ))
            }
          </div>
        </div>

        {/* Economic overview */}
        <div className="economic-overview">
          <h4>Economic Overview</h4>
          <p>
            {state.overview || 
              `${state.name}'s ${selectedIndicator === 'UNRATE' ? 'unemployment rate' : 
                selectedIndicator === 'MSPUS' ? 'housing market' : 'personal income'} 
              shows ${state.value !== null ? 
                selectedIndicator === 'UNRATE' ? 
                  state.value < 4 ? 'strong performance' : 
                  state.value < 6 ? 'moderate performance' : 'challenges' : 
                selectedIndicator === 'MSPUS' ? 
                  state.value > 400000 ? 'high prices' : 'moderate prices' : 
                  state.value > 60000 ? 'high income levels' : 'moderate income levels' 
                : 'no data available'}.`}
          </p>
        </div>
      </div>
    );
  };

  return (
    <div className="regional-data indicator-page">
      <h2>Regional Economic Data</h2>

      <div className="controls">
        <div className="selector">
          <label htmlFor="indicator-select">Select Indicator: </label>
          <select 
            id="indicator-select" 
            value={selectedIndicator} 
            onChange={handleIndicatorChange}
          >
            {indicators.map(indicator => (
              <option key={indicator.id} value={indicator.id}>
                {indicator.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      {loading ? (
        <div className="loading">Loading regional data...</div>
      ) : error ? (
        <div className="error">{error}</div>
      ) : (
        <div className="regional-content">
          <div className="map-and-details">
            <div className="us-map-container" ref={mapRef}>
              {/* Base layer: React Simple Maps for accurate geography */}
              <div className="map-base-layer">
                <ComposableMap projection="geoAlbersUsa">
                  <Geographies geography={geoUrl}>
                    {({ geographies }) => 
                      geographies.map(geo => {
                        const stateCode = geo.properties.STUSPS;
                        const stateColor = stateColors[stateCode] 
                          ? stateColors[stateCode].color 
                          : '#D3D3D3'; // Default gray for no data

                        return (
                          <Geography
                            key={geo.rsmKey}
                            geography={geo}
                            fill={stateColor}
                            stroke="#FFFFFF"
                            strokeWidth={0.5}
                            style={{
                              default: { outline: "none" },
                              hover: { outline: "none" }
                            }}
                          />
                        );
                      })
                    }
                  </Geographies>
                </ComposableMap>
              </div>

              {/* Interactive layer: Use the same geographies but with transparent fill and interaction handlers */}
              <div className="map-interactive-layer">
                <ComposableMap projection="geoAlbersUsa">
                  <Geographies geography={geoUrl}>
                    {({ geographies }) => 
                      geographies.map(geo => {
                        const stateCode = geo.properties.STUSPS;

                        return (
                          <Geography
                            key={geo.rsmKey}
                            geography={geo}
                            fill="transparent"
                            stroke="transparent"
                            style={{
                              default: { outline: "none", cursor: "pointer" },
                              hover: { 
                                outline: "none", 
                                stroke: "#333",
                                strokeWidth: 1.5,
                                cursor: "pointer"
                              },
                              pressed: { outline: "none" }
                            }}
                            onMouseEnter={() => {
                              setHoveredState(stateCode);
                            }}
                            onMouseLeave={() => {
                              setHoveredState(null);
                            }}
                          />
                        );
                      })
                    }
                  </Geographies>
                </ComposableMap>
              </div>
            </div>
            <div className="state-info-panel">
              {renderStateInfo()}
            </div>
          </div>

          <div className="legend">
            <h3>Legend</h3>
            <div className="legend-item">
              {selectedIndicator === 'UNRATE' ? (
                <>
                  <div className="legend-color" style={{ background: 'rgb(0, 255, 0)' }}></div>
                  <span>Low Unemployment</span>
                  <div className="legend-color" style={{ background: 'rgb(255, 0, 0)' }}></div>
                  <span>High Unemployment</span>
                </>
              ) : (
                <>
                  <div className="legend-color" style={{ background: 'rgb(0, 255, 0)' }}></div>
                  <span>High {selectedIndicator === 'MSPUS' ? 'House Prices' : 'Income'}</span>
                  <div className="legend-color" style={{ background: 'rgb(255, 0, 0)' }}></div>
                  <span>Low {selectedIndicator === 'MSPUS' ? 'House Prices' : 'Income'}</span>
                </>
              )}
              <div className="legend-color" style={{ background: '#D3D3D3' }}></div>
              <span>No Data</span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RegionalData;
