import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { 
  ComposableMap, 
  Geographies, 
  Geography, 
  ZoomableGroup 
} from "react-simple-maps";
// Import d3-scale for color mapping
import { scaleLinear } from "d3-scale";

// Use specific USA TopoJSON that includes state postal codes
const geoUrl = "https://cdn.jsdelivr.net/npm/us-atlas@3/states-10m.json";

/**
 * RegionalData component for displaying economic data by state on an interactive map
 * Using react-simple-maps for the visualization
 */
const RegionalData = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [hoveredState, setHoveredState] = useState(null);
  const [selectedIndicator, setSelectedIndicator] = useState('UNRATE'); // Default to Unemployment Rate
  const [stateData, setStateData] = useState({});
  const [minMaxValues, setMinMaxValues] = useState({ min: 0, max: 10 });

  // Available indicators to select from
  const indicators = [
    { id: 'UNRATE', name: 'Unemployment Rate' },
    { id: 'MSPUS', name: 'Median House Price' },
    { id: 'PCPI', name: 'Per Capita Personal Income' }
  ];

  // Create color scale based on the selected indicator and data range
  const colorScale = useMemo(() => {
    if (selectedIndicator === 'UNRATE') {
      // For unemployment: low (green) is good, high (red) is bad
      return scaleLinear()
        .domain([minMaxValues.min, minMaxValues.max])
        .range(["#00ff00", "#ff0000"]);
    } else {
      // For prices/income: high (green) is good, low (red) is bad
      return scaleLinear()
        .domain([minMaxValues.min, minMaxValues.max])
        .range(["#ff0000", "#00ff00"]);
    }
  }, [selectedIndicator, minMaxValues]);

  // Function to generate simulated data for development/testing
  const generateSimulatedData = () => {
    // Define state codes and names
    const stateCodes = [
      'AL', 'AK', 'AZ', 'AR', 'CA', 'CO', 'CT', 'DE', 'FL', 'GA', 
      'HI', 'ID', 'IL', 'IN', 'IA', 'KS', 'KY', 'LA', 'ME', 'MD', 
      'MA', 'MI', 'MN', 'MS', 'MO', 'MT', 'NE', 'NV', 'NH', 'NJ', 
      'NM', 'NY', 'NC', 'ND', 'OH', 'OK', 'OR', 'PA', 'RI', 'SC', 
      'SD', 'TN', 'TX', 'UT', 'VT', 'VA', 'WA', 'WV', 'WI', 'WY', 'DC'
    ];

    const stateNames = {
      'AL': 'Alabama', 'AK': 'Alaska', 'AZ': 'Arizona', 'AR': 'Arkansas',
      'CA': 'California', 'CO': 'Colorado', 'CT': 'Connecticut', 'DE': 'Delaware',
      'FL': 'Florida', 'GA': 'Georgia', 'HI': 'Hawaii', 'ID': 'Idaho',
      'IL': 'Illinois', 'IN': 'Indiana', 'IA': 'Iowa', 'KS': 'Kansas',
      'KY': 'Kentucky', 'LA': 'Louisiana', 'ME': 'Maine', 'MD': 'Maryland',
      'MA': 'Massachusetts', 'MI': 'Michigan', 'MN': 'Minnesota', 'MS': 'Mississippi',
      'MO': 'Missouri', 'MT': 'Montana', 'NE': 'Nebraska', 'NV': 'Nevada',
      'NH': 'New Hampshire', 'NJ': 'New Jersey', 'NM': 'New Mexico', 'NY': 'New York',
      'NC': 'North Carolina', 'ND': 'North Dakota', 'OH': 'Ohio', 'OK': 'Oklahoma',
      'OR': 'Oregon', 'PA': 'Pennsylvania', 'RI': 'Rhode Island', 'SC': 'South Carolina',
      'SD': 'South Dakota', 'TN': 'Tennessee', 'TX': 'Texas', 'UT': 'Utah',
      'VT': 'Vermont', 'VA': 'Virginia', 'WA': 'Washington', 'WV': 'West Virginia',
      'WI': 'Wisconsin', 'WY': 'Wyoming', 'DC': 'District of Columbia'
    };

    // Define different value ranges based on indicator
    let minVal, maxVal;
    switch (selectedIndicator) {
      case 'UNRATE':
        minVal = 1.9;  // Minimum unemployment rate
        maxVal = 5.8;  // Maximum unemployment rate
        break;
      case 'MSPUS':
        minVal = 150000;  // Minimum house price
        maxVal = 750000;  // Maximum house price
        break;
      case 'PCPI':
        minVal = 35000;  // Minimum per capita income
        maxVal = 80000;  // Maximum per capita income
        break;
      default:
        minVal = 0;
        maxVal = 100;
    }

    // Update min/max state for the color scale
    setMinMaxValues({ min: minVal, max: maxVal });

    // Generate data for each state
    const statesData = {};
    stateCodes.forEach(code => {
      // Randomly decide if some states have no data
      const hasData = Math.random() > 0.1; // 10% chance of no data

      if (hasData) {
        // Generate a random value within the range
        const value = minVal + Math.random() * (maxVal - minVal);
        const roundedValue = selectedIndicator === 'UNRATE' 
          ? parseFloat(value.toFixed(1))  // 1 decimal for rates
          : Math.round(value);            // Whole numbers for money values

        statesData[code] = {
          code,
          name: stateNames[code],
          value: roundedValue,
          date: '2025-01-15'
        };
      } else {
        statesData[code] = {
          code,
          name: stateNames[code],
          value: null,
          date: null
        };
      }
    });

    return statesData;
  };

  // Fetch or generate data when indicator changes
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);

        let statesData = {};

        // Try to fetch from API first
        try {
          const response = await axios.get(`/api/regional/${selectedIndicator}`);

          // Process API response into the format we need
          if (response.data && response.data.states) {
            const values = [];

            response.data.states.forEach(state => {
              if (state.value !== null && state.value !== undefined) {
                values.push(state.value);
                statesData[state.code] = state;
              }
            });

            // Set min/max values for color scale
            if (values.length > 0) {
              setMinMaxValues({
                min: Math.min(...values),
                max: Math.max(...values)
              });
            }
          }
        } catch (err) {
          console.warn("API call failed, using simulated data:", err);
          // Use simulated data if API fails
          statesData = generateSimulatedData();
        }

        setStateData(statesData);
        setLoading(false);
      } catch (err) {
        console.error('Error setting up regional data:', err);
        setError('Failed to load regional data. Please try again later.');
        setLoading(false);
      }
    };

    fetchData();
  }, [selectedIndicator]);

  // Handle dropdown change
  const handleIndicatorChange = (e) => {
    setSelectedIndicator(e.target.value);
  };

  // Format value for display based on indicator type
  const formatValue = (value, indicator) => {
    if (value === null || value === undefined) return 'No data available';

    switch (indicator) {
      case 'UNRATE':
        return `${value.toFixed(1)}%`;
      case 'MSPUS':
        return `$${value.toLocaleString()}`;
      case 'PCPI':
        return `$${value.toLocaleString()}`;
      default:
        return value.toLocaleString();
    }
  };

  // Get indicator display name
  const getIndicatorName = (indicator) => {
    const ind = indicators.find(i => i.id === indicator);
    return ind ? ind.name : indicator;
  };

  // Handle mouse enter on state
  const handleStateEnter = (geo) => {
    const stateCode = geo.properties.postal;
    setHoveredState(stateCode);
  };

  // Handle mouse leave
  const handleStateLeave = () => {
    setHoveredState(null);
  };

  // Render state information panel
  const renderStateInfo = () => {
    if (!hoveredState || !stateData[hoveredState]) {
      return null;
    }

    const state = stateData[hoveredState];

    return (
      <div className="state-details">
        <h3>{state.name}</h3>
        <div className="state-metric">
          <h4>{getIndicatorName(selectedIndicator)}</h4>
          <div className="metric-value">
            {formatValue(state.value, selectedIndicator)}
          </div>
          <div className="metric-date">
            As of {state.date || 'N/A'}
          </div>
        </div>
        <div className="economic-overview">
          <h4>Economic Overview</h4>
          <p>
            {state.overview || 
              `${state.name}'s ${selectedIndicator === 'UNRATE' ? 'unemployment rate' : 
                selectedIndicator === 'MSPUS' ? 'housing market' : 'personal income'} 
              shows ${state.value !== null ? 
                selectedIndicator === 'UNRATE' ? 
                  state.value < 4 ? 'strong performance' : 
                  state.value < 6 ? 'moderate performance' : 'challenges' : 
                selectedIndicator === 'MSPUS' ? 
                  state.value > 400000 ? 'high prices' : 'moderate prices' : 
                  state.value > 60000 ? 'high income levels' : 'moderate income levels' 
                : 'no data available'}.`}
          </p>
        </div>
      </div>
    );
  };

  return (
    <div className="regional-data indicator-page">
      <h2>Regional Economic Data</h2>

      <div className="controls">
        <div className="selector">
          <label htmlFor="indicator-select">Select Indicator: </label>
          <select 
            id="indicator-select" 
            value={selectedIndicator} 
            onChange={handleIndicatorChange}
          >
            {indicators.map(indicator => (
              <option key={indicator.id} value={indicator.id}>
                {indicator.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      {loading ? (
        <div className="loading">Loading regional data...</div>
      ) : error ? (
        <div className="error">{error}</div>
      ) : (
        <div className="regional-content">
          <div className="map-and-details">
            <div className="us-map-container">
              {/* Using react-simple-maps with proper configuration */}
              <ComposableMap 
                projection="geoAlbersUsa"
                projectionConfig={{ scale: 1100 }}
                width={800}
                height={500}
                style={{ width: "100%", height: "auto" }}
              >
                <Geographies geography={geoUrl}>
                  {({ geographies }) =>
                    geographies.map(geo => {
                      // Get state code from properties
                      const stateCode = geo.properties.postal;
                      const state = stateData[stateCode];
                      const isHovered = hoveredState === stateCode;

                      // Set fill color based on data
                      let fillColor = "#D3D3D3"; // Default gray for no data
                      if (state && state.value !== null && state.value !== undefined) {
                        fillColor = colorScale(state.value);
                      }

                      return (
                        <Geography
                          key={geo.rsmKey}
                          geography={geo}
                          fill={fillColor}
                          stroke="#FFFFFF"
                          strokeWidth={0.5}
                          onClick={() => {
                            // Optionally add click handler
                            console.log(`Clicked on ${stateCode}`);
                          }}
                          onMouseEnter={() => handleStateEnter(geo)}
                          onMouseLeave={handleStateLeave}
                          style={{
                            default: { outline: "none" },
                            hover: { 
                              outline: "none", 
                              stroke: "#000000", 
                              strokeWidth: 1.5,
                              cursor: "pointer"
                            },
                            pressed: { outline: "none" }
                          }}
                        />
                      );
                    })
                  }
                </Geographies>
              </ComposableMap>
            </div>
            <div className="state-info-panel">
              {hoveredState ? renderStateInfo() : (
                <div className="default-info">
                  <h3>Hover over a state to view details</h3>
                  <p>Currently displaying: {getIndicatorName(selectedIndicator)}</p>
                </div>
              )}
            </div>
          </div>

          <div className="legend">
            <h3>Legend</h3>
            <div className="legend-items">
              {selectedIndicator === 'UNRATE' ? (
                <>
                  <div className="legend-item">
                    <div className="legend-color" style={{ background: '#00ff00' }}></div>
                    <span>Low Unemployment ({minMaxValues.min?.toFixed(1)}%)</span>
                  </div>
                  <div className="legend-item">
                    <div className="legend-color" style={{ background: '#ff0000' }}></div>
                    <span>High Unemployment ({minMaxValues.max?.toFixed(1)}%)</span>
                  </div>
                </>
              ) : (
                <>
                  <div className="legend-item">
                    <div className="legend-color" style={{ background: '#00ff00' }}></div>
                    <span>High {selectedIndicator === 'MSPUS' ? 'House Prices' : 'Income'} 
                    ({selectedIndicator === 'MSPUS' || selectedIndicator === 'PCPI' ? 
                      '$' + minMaxValues.max?.toLocaleString() : 
                      minMaxValues.max})</span>
                  </div>
                  <div className="legend-item">
                    <div className="legend-color" style={{ background: '#ff0000' }}></div>
                    <span>Low {selectedIndicator === 'MSPUS' ? 'House Prices' : 'Income'}
                    ({selectedIndicator === 'MSPUS' || selectedIndicator === 'PCPI' ? 
                      '$' + minMaxValues.min?.toLocaleString() : 
                      minMaxValues.min})</span>
                  </div>
                </>
              )}
              <div className="legend-item">
                <div className="legend-color" style={{ background: '#D3D3D3' }}></div>
                <span>No Data</span>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RegionalData;
