import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  ComposableMap, 
  Geographies, 
  Geography, 
  ZoomableGroup 
} from "react-simple-maps";
import { scaleLinear } from "d3-scale";

// URL for US states GeoJSON data - standard format for geographic mapping
const geoUrl = "https://cdn.jsdelivr.net/npm/us-atlas@3/states-10m.json";

/**
 * RegionalData component for displaying economic data by state on an interactive map
 * Uses react-simple-maps library instead of direct SVG path elements
 */
const RegionalData = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [hoveredState, setHoveredState] = useState(null);
  const [selectedIndicator, setSelectedIndicator] = useState('MSPUS'); // Default to Median House Price
  const [stateData, setStateData] = useState({});
  const [colorScale, setColorScale] = useState(() => scaleLinear().domain([0, 1]).range(["#ff0000", "#00ff00"]));
  const [minValue, setMinValue] = useState(null);
  const [maxValue, setMaxValue] = useState(null);

  // Available indicators
  const indicators = [
    { id: 'UNRATE', name: 'Unemployment Rate' },
    { id: 'MSPUS', name: 'Median House Price' },
    { id: 'PCPI', name: 'Per Capita Personal Income' }
  ];

  // Fetch data for all states
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // For development/testing if API is not available
        let response;
        try {
          response = await axios.get(`/api/regional/${selectedIndicator}`);
        } catch (err) {
          // Fallback to simulated data if API fails
          console.warn("Using simulated data due to API error:", err);
          response = { data: generateSimulatedData(selectedIndicator) };
        }

        // Process data for map coloring
        const states = {};
        const values = [];

        // Extract valid values for creating color scale
        response.data.states.forEach(state => {
          states[state.code] = state;
          if (state.value !== null && state.value !== undefined) {
            values.push(state.value);
          }
        });

        if (values.length > 0) {
          // Get min/max values for normalization
          const min = Math.min(...values);
          const max = Math.max(...values);
          setMinValue(min);
          setMaxValue(max);

          // Create color scale based on indicator type
          if (selectedIndicator === 'UNRATE') {
            // For unemployment, low is good (green), high is bad (red)
            setColorScale(scaleLinear()
              .domain([min, max])
              .range(["rgb(0, 255, 0)", "rgb(255, 0, 0)"]));
          } else {
            // For income/housing prices, high is good (green), low is bad (red)
            setColorScale(scaleLinear()
              .domain([min, max])
              .range(["rgb(255, 0, 0)", "rgb(0, 255, 0)"]));
          }
        }

        setStateData(states);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching regional data:', err);
        setError('Failed to load regional data. Please try again later.');
        setLoading(false);
      }
    };

    fetchData();
  }, [selectedIndicator]);

  // Generate simulated data for testing when API is not available
  const generateSimulatedData = (indicator) => {
    const stateCodes = [
      'AL', 'AK', 'AZ', 'AR', 'CA', 'CO', 'CT', 'DE', 'FL', 'GA', 
      'HI', 'ID', 'IL', 'IN', 'IA', 'KS', 'KY', 'LA', 'ME', 'MD', 
      'MA', 'MI', 'MN', 'MS', 'MO', 'MT', 'NE', 'NV', 'NH', 'NJ', 
      'NM', 'NY', 'NC', 'ND', 'OH', 'OK', 'OR', 'PA', 'RI', 'SC', 
      'SD', 'TN', 'TX', 'UT', 'VT', 'VA', 'WA', 'WV', 'WI', 'WY', 'DC'
    ];

    const stateNames = {
      'AL': 'Alabama', 'AK': 'Alaska', 'AZ': 'Arizona', 'AR': 'Arkansas',
      'CA': 'California', 'CO': 'Colorado', 'CT': 'Connecticut', 'DE': 'Delaware',
      'FL': 'Florida', 'GA': 'Georgia', 'HI': 'Hawaii', 'ID': 'Idaho',
      'IL': 'Illinois', 'IN': 'Indiana', 'IA': 'Iowa', 'KS': 'Kansas',
      'KY': 'Kentucky', 'LA': 'Louisiana', 'ME': 'Maine', 'MD': 'Maryland',
      'MA': 'Massachusetts', 'MI': 'Michigan', 'MN': 'Minnesota', 'MS': 'Mississippi',
      'MO': 'Missouri', 'MT': 'Montana', 'NE': 'Nebraska', 'NV': 'Nevada',
      'NH': 'New Hampshire', 'NJ': 'New Jersey', 'NM': 'New Mexico', 'NY': 'New York',
      'NC': 'North Carolina', 'ND': 'North Dakota', 'OH': 'Ohio', 'OK': 'Oklahoma',
      'OR': 'Oregon', 'PA': 'Pennsylvania', 'RI': 'Rhode Island', 'SC': 'South Carolina',
      'SD': 'South Dakota', 'TN': 'Tennessee', 'TX': 'Texas', 'UT': 'Utah',
      'VT': 'Vermont', 'VA': 'Virginia', 'WA': 'Washington', 'WV': 'West Virginia',
      'WI': 'Wisconsin', 'WY': 'Wyoming', 'DC': 'District of Columbia'
    };

    let minVal, maxVal;

    switch (indicator) {
      case 'UNRATE':
        minVal = 2.5;
        maxVal = 8.0;
        break;
      case 'MSPUS':
        minVal = 150000;
        maxVal = 750000;
        break;
      case 'PCPI':
        minVal = 35000;
        maxVal = 80000;
        break;
      default:
        minVal = 0;
        maxVal = 100;
    }

    const states = stateCodes.map(code => {
      // Generate a random value between min and max
      const value = Math.round(minVal + Math.random() * (maxVal - minVal));
      const date = '2025-01-15'; // Simulated recent date

      return {
        code,
        name: stateNames[code],
        value,
        date
      };
    });

    return {
      indicator,
      name: indicators.find(ind => ind.id === indicator)?.name || indicator,
      units: indicator === 'UNRATE' ? 'percent' : 'dollars',
      states
    };
  };

  // Handle indicator change
  const handleIndicatorChange = (e) => {
    setSelectedIndicator(e.target.value);
  };

  // Handle state hover
  const handleStateMouseEnter = (geo) => {
    // Get the state code from the geo properties
    const stateCode = geo.properties.postal;
    setHoveredState(stateCode);
  };

  // Handle mouse leave
  const handleStateMouseLeave = () => {
    setHoveredState(null);
  };

  // Format value based on indicator type
  const formatValue = (value, indicator) => {
    if (value === null || value === undefined) return 'No data available';

    switch (indicator) {
      case 'UNRATE':
        return `${value.toFixed(1)}%`;
      case 'MSPUS':
        return `$${value.toLocaleString()}`;
      case 'PCPI':
        return `$${value.toLocaleString()}`;
      default:
        return value.toLocaleString();
    }
  };

  // Get indicator friendly name
  const getIndicatorName = (indicator) => {
    const ind = indicators.find(i => i.id === indicator);
    return ind ? ind.name : indicator;
  };

  // Show hovered state info
  const renderStateInfo = () => {
    if (!hoveredState || !stateData[hoveredState]) {
      return null;
    }

    const state = stateData[hoveredState];

    return (
      <div className="state-details">
        <h3>{state.name}</h3>
        <div className="state-metric">
          <h4>{getIndicatorName(selectedIndicator)}</h4>
          <div className="metric-value">
            {formatValue(state.value, selectedIndicator)}
          </div>
          <div className="metric-date">
            As of {state.date || 'N/A'}
          </div>
        </div>
        <div className="economic-overview">
          <h4>Economic Overview</h4>
          <p>
            {state.overview || 
              `${state.name}'s ${selectedIndicator === 'UNRATE' ? 'unemployment rate' : 
                selectedIndicator === 'MSPUS' ? 'housing market' : 'personal income'} 
              shows ${state.value !== null ? 
                selectedIndicator === 'UNRATE' ? 
                  state.value < 4 ? 'strong performance' : 
                  state.value < 6 ? 'moderate performance' : 'challenges' : 
                selectedIndicator === 'MSPUS' ? 
                  state.value > 400000 ? 'high prices' : 'moderate prices' : 
                  state.value > 60000 ? 'high income levels' : 'moderate income levels' 
                : 'no data available'}.`}
          </p>
        </div>
      </div>
    );
  };

  return (
    <div className="regional-data indicator-page">
      <h2>Regional Economic Data</h2>

      <div className="controls">
        <div className="selector">
          <label htmlFor="indicator-select">Select Indicator: </label>
          <select 
            id="indicator-select" 
            value={selectedIndicator} 
            onChange={handleIndicatorChange}
          >
            {indicators.map(indicator => (
              <option key={indicator.id} value={indicator.id}>
                {indicator.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      {loading ? (
        <div className="loading">Loading regional data...</div>
      ) : error ? (
        <div className="error">{error}</div>
      ) : (
        <div className="regional-content">
          <div className="map-and-details">
            <div className="us-map-container">
              <ComposableMap 
                projection="geoAlbersUsa"
                projectionConfig={{ scale: 1000 }}
              >
                <ZoomableGroup>
                  <Geographies geography={geoUrl}>
                    {({ geographies }) =>
                      geographies.map(geo => {
                        const stateCode = geo.properties.postal;
                        const state = stateData[stateCode];

                        // Default gray color for states with no data
                        let fillColor = "#D3D3D3";

                        // Apply colorScale for states with data
                        if (state && state.value !== null && state.value !== undefined) {
                          fillColor = colorScale(state.value);
                        }

                        return (
                          <Geography
                            key={geo.rsmKey}
                            geography={geo}
                            fill={fillColor}
                            stroke="#FFFFFF"
                            strokeWidth={0.5}
                            style={{
                              default: {
                                outline: "none"
                              },
                              hover: {
                                outline: "none",
                                stroke: "#000",
                                strokeWidth: 1.5,
                                cursor: "pointer"
                              },
                              pressed: {
                                outline: "none"
                              }
                            }}
                            onMouseEnter={() => handleStateMouseEnter(geo)}
                            onMouseLeave={handleStateMouseLeave}
                          />
                        );
                      })
                    }
                  </Geographies>
                </ZoomableGroup>
              </ComposableMap>
            </div>
            <div className="state-info-panel">
              {hoveredState ? renderStateInfo() : (
                <div className="default-info">
                  <h3>Hover over a state to view details</h3>
                  <p>Currently displaying: {getIndicatorName(selectedIndicator)}</p>
                </div>
              )}
            </div>
          </div>

          <div className="legend">
            <h3>Legend</h3>
            <div className="legend-items">
              {selectedIndicator === 'UNRATE' ? (
                <>
                  <div className="legend-item">
                    <div className="legend-color" style={{ background: 'rgb(0, 255, 0)' }}></div>
                    <span>Low Unemployment ({minValue?.toFixed(1)}%)</span>
                  </div>
                  <div className="legend-item">
                    <div className="legend-color" style={{ background: 'rgb(255, 0, 0)' }}></div>
                    <span>High Unemployment ({maxValue?.toFixed(1)}%)</span>
                  </div>
                </>
              ) : (
                <>
                  <div className="legend-item">
                    <div className="legend-color" style={{ background: 'rgb(0, 255, 0)' }}></div>
                    <span>High {selectedIndicator === 'MSPUS' ? 'House Prices' : 'Income'} 
                    ({selectedIndicator === 'MSPUS' || selectedIndicator === 'PCPI' ? '$' + maxValue?.toLocaleString() : maxValue})</span>
                  </div>
                  <div className="legend-item">
                    <div className="legend-color" style={{ background: 'rgb(255, 0, 0)' }}></div>
                    <span>Low {selectedIndicator === 'MSPUS' ? 'House Prices' : 'Income'}
                    ({selectedIndicator === 'MSPUS' || selectedIndicator === 'PCPI' ? '$' + minValue?.toLocaleString() : minValue})</span>
                  </div>
                </>
              )}
              <div className="legend-item">
                <div className="legend-color" style={{ background: '#D3D3D3' }}></div>
                <span>No Data</span>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RegionalData;
